import React, { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { FaTimes } from 'react-icons/fa'; // import the icon

const dummyImages = Array.from({ length: 20 }, (_, i) => `https://picsum.photos/200/200?random=${i + 1}`);


const Image = () => {
    const {username}=useParams();
    const navigate =useNavigate();
    const [images, setImages] = useState(dummyImages);
    const[previewImage,setPreviewImage]=useState(null);
    const handleDelete = (indexToDelete) => {
        setImages((prev) => prev.filter((_, idx) => idx !== indexToDelete));
      };
    const handleFullImage = (url) => {
        navigate(`/full-image?src=${encodeURIComponent(url)}`);
      };
  return (

    <div className="p-4 relative">
    <h2 className="text-xl font-bold mb-4">Hi, {username}</h2>

    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
      {dummyImages.map((img, idx) => (
        <div key={idx} className="relative border rounded overflow-hidden shadow">
          <button
            className="absolute top-1 right-1 bg-white rounded-full p-1 text-sm hover:bg-gray-200"
            onClick={() => handleDelete(idx)}
          >
            <FaTimes />
          </button>
          <img src={img} alt={`Random ${idx}`} className="w-full h-40 object-cover" />
          <div className="flex justify-between px-2 py-2">
            <button
              className="bg-blue-500 text-white px-2 py-1 rounded text-sm"
              onClick={() => setPreviewImage(img)}
            >
              Preview
            </button>
            <button
              className="bg-green-500 text-white px-2 py-1 rounded text-sm"
              onClick={() => handleFullImage(img)}
            >
              Full Image
            </button>
          </div>
        </div>
      ))}
    </div>

    {/* Preview Modal */}
    {previewImage && (
      <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
        <div className="bg-white p-4 rounded relative shadow-lg">
          <button
            className="absolute top-1 right-1 text-lg hover:bg-gray-200 rounded-full p-1"
            onClick={() => setPreviewImage(null)}
          >
            <FaTimes />
          </button>
          <img src={previewImage} alt="Preview" className="max-w-xs sm:max-w-md" />
        </div>
      </div>
    )}
  </div>
  )
}

export default Image
