import React from 'react';
import { useLocation } from 'react-router-dom';

const FullImage = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const imageUrl = searchParams.get('src');

  return (
    <div className="w-screen h-screen bg-gray-100 flex flex-col items-center justify-start overflow-hidden">
      <h1 className="text-2xl font-bold mt-4 mb-6 text-center">Your Full Image</h1>
      {imageUrl ? (
        <div className="flex-grow flex items-center justify-center w-full px-4">
          <img
            src={imageUrl}
            alt="Full Size"
            className="max-h-[80vh] w-auto object-contain rounded shadow-md"
          />
        </div>
      ) : (
        <p className="text-gray-500">No image to display.</p>
      )}
    </div>
  );
};

export default FullImage;
