import React, {  useState } from 'react';

const Sidebar = ({ groups, setGroups, setSelectedGroup }) => {
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupColor, setNewGroupColor] = useState('blue');
  const [showPopup, setShowPopup] = useState(false);
  const customColors = ['#B38BFA', '#FF79F2', '#43E6FC', '#F19576', '#0047FF', '#6691FF'];
  const plus = "/Assets/Images/plus.png";

  const handleAddGroup = () => {
    const getInitials = (name) => {
      return name
        .split(" ")
        .filter(word => word.length > 0)
        .map(word => word[0].toUpperCase())
        .join("");
    };

    const newGroup = {
      initial: getInitials(newGroupName),
      name: newGroupName,
      color: newGroupColor
    };
    setGroups([newGroup, ...groups]);
    


    setShowPopup(false); 
    setNewGroupName('');
    setNewGroupColor('blue');
  };


  return (

    <div className='bg-white h-[100%] w-[25%] flex flex-col p-6 gap-4'>
      <h1 className="text-3xl flex justify-center items-center font-roboto w-full h-[10%]">Pocket Notes</h1>
      <div className="flex flex-col overflow-y-auto h-[100%] bg-pink-700 relative w-full h-[90%]">

        {groups.map((note, index) => (
          <div key={index} className='flex items-center gap-4 p-4 rounded-lg hover:bg-gray-100'
            onClick={() => setSelectedGroup(note)}
          >
            <div className=" text-white w-[50px] h-[50px] flex items-center justify-center rounded-full font-bold" style={{ backgroundColor: note.color }}>
              {note.initial}
            </div>
            <div className="text-md text-black font-medium">{note.name}</div>
          </div>


        ))}
      </div>
      <div className='flex justify-end items-center h-[8%] w-[6%] absolute'>
        <button className=' bg-[#16008B] w-full h-full rounded-full flex items-center justify-center'
          onClick={() => setShowPopup(true)}>
          <img src={plus} alt="Add" className='absolute' />
        </button>
      </div>

      {showPopup && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg w-1/3">
            <label className="w-1/3 text-md font-semibold flex" >Create New Group</label>
            <div className="flex items-center gap-6 mb-4">
              <label className="w-1/3 text-md font-semibold">Group Name</label>
              <input
                type="text"
                placeholder="Enter Group Name"
                value={newGroupName}
                onChange={(e) => setNewGroupName(e.target.value)}
                className="border p-2 pl-3 w-2/3 "
              />

            </div>

            <div className="flex gap-4 mb-4">
              <label className="w-1/3 text-md font-semibold">Choose color</label>
              {customColors.map((color, index) => (
                <div
                  key={index}
                  // className={`w-10 h-10 rounded-full cursor-pointer bg-${color}-500`}
                  className="w-10 h-10 rounded-full cursor-pointer border-2"
                  style={{ backgroundColor: color }}
                  onClick={() => setNewGroupColor(color)}
                />
              ))}
            </div>
            <div className="flex justify-end gap-4">
              <button onClick={() => setShowPopup(false)} className="px-4 py-2 bg-blue-500 rounded-md">Cancel</button>
              {/* <button onClick={handleAddGroup} disabled={!newGroupName.trim() || !newGroupColor} className="px-4 py-2 bg-blue-500 text-white rounded-md">Create</button> */}
              <button
                onClick={handleAddGroup}
                disabled={!newGroupName.trim() || !newGroupColor}
                className={`px-4 py-2 rounded-md text-white ${(!newGroupName.trim() || !newGroupColor) ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-500'
                  }`}
              > Create
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
