import React from 'react';
import { useParams } from 'react-router-dom';

const GroupChat = () => {
  const { groupId } = useParams();

  // This could come from a group list or context
  const groupMap = {
    'my-notes': { name: 'My Notes', initials: 'MN' },
    'css': { name: 'CSS Notes', initials: 'CN' },
    'html': { name: 'HTML grp', initials: 'HG' },
  };

  const group = groupMap[groupId];

  return (
    <div className="flex flex-col h-full bg-[#DAE5F5] w-full">
      {/* Header */}
      <div className="bg-blue-800 text-white p-4 flex items-center gap-4">
        <div className="w-10 h-10 rounded-full bg-white text-blue-800 font-bold flex items-center justify-center">
          {group?.initials}
        </div>
        <h2 className="text-xl font-semibold">{group?.name}</h2>
      </div>

      {/* Chat messages */}
      <div className="flex-1 p-4 overflow-y-auto space-y-4">
        {/* Map chat messages here */}
        <div className="bg-white p-4 rounded shadow">This is a sample chat message.</div>
      </div>

      {/* Chat input */}
      <div className="bg-blue-800 p-4">
        <textarea
          placeholder="Enter your text here............"
          className="w-full p-2 rounded resize-none"
        />
      </div>
    </div>
  );
};

export default GroupChat;
