import React, { useState, useCallback } from 'react';

function Counter({ onIncrement }) {
    console.log('Counter rendered');
    return (
      <button onClick={onIncrement}>Increment</button>
    );
  }
  
const AdvanceHooks = () => {
    const [count, setCount] = useState(0);

  // Memoize the function to avoid unnecessary re-creations
  const increment = useCallback(() => {
    setCount(prevCount => prevCount + 1);
  }, []);  // The empty array ensures the function is created once

  // Memoize a derived value to avoid recalculating on each render
  const expensiveCalculation = useMemo(() => {
    console.log('Expensive calculation running...');
    return count * 2;  // Example of an expensive calculation
  }, [count]);  // Only recalculate if `count` changes

  return (
    <div>
      <h1>Count: {count}</h1>
      
      <Counter onIncrement={increment} />
    </div>
  )
}

export default AdvanceHooks
