import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import ChatHeader from '../component/chatHeader';
import ChatPanel from '../component/chatPanel';


const GroupChat = ({group}) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const send ='./Assets/Images/send.png'

  const formatDate = (date) => {
    const day = date.getDate();
    const month = date.toLocaleString('default', { month: 'long' }); // e.g., March
    const year = date.getFullYear(); 
    const time = date.toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
    }); 
    return `${day} ${month} ${year} . ${time}`;
  };
  

  const handleSend = () => {
    if (input.trim() === '') return;

    const newMessage = {
        text: input,
        timestamp: formatDate(new Date()), // current date and time
      };

    // setMessages([...messages,newMessage]);
    onSendMessage(group.name, newMessage);

    setInput('');
  };

  return (
    <div className='flex flex-col h-full w-full ' >
      <ChatHeader initial={group.initial} name={group.name} color={group.color}/>
      <ChatPanel messages={messages} />
      <div className='h-[10%] bg-[#DAE5F5] flex items-center px-6'>
        <div className='relative flex-1'>

        <textarea
          className='w-full p-4 pr-12 rounded-xl border border-gray-300 '
          type="text"
          placeholder="Enter your text here..."
          value={input}
          
          onChange={(e) => setInput(e.target.value)}
        //   onKeyDown={(e) => e.key === 'Enter' && handleSend()}

        onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault(); // prevent new line
              handleSend();
            }
        }}
        />
        <button className='absolute right-3 top-1/3 transform -translate-y-1/2 bg-[#ffffff] font-semibold   '
          onClick={handleSend}
        >
             <img src={send} alt="Add"  />
          
        </button>
        </div>
      </div>
    </div>
  );
};

export default GroupChat;
