import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const notes = [
  
];

const Sidebar = () => {
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupColor, setNewGroupColor] = useState('blue');
  const [showPopup, setShowPopup] = useState(false);
  const [groups, setGroups] = useState(notes);
  const [selectedGroup, setSelectedGroup] = useState(null); 

  const plus = "/Assets/Images/plus.png";
  
  const handleAddGroup = () => {
    const newGroup = {
      initial: newGroupName.slice(0, 2).toUpperCase(),
      name: newGroupName,
      color: newGroupColor
    };
    setGroups([newGroup,...groups, ]);
    setShowPopup(false); // Close popup after adding the group
    setNewGroupName('');
    setNewGroupColor('blue');
  };
  return (
    <div className='bg-white h-full w-[20%] flex flex-col p-6'>
      <h1 className="text-3xl mb-4 w-full h-[8%] font-roboto flex justify-center items-center">Pocket Notes</h1>
      <div className="flex flex-col gap-1 overflow-y-auto h-[70%]">
      
        {notes.map((note, index) => (
          <Link key={index} to={`/pocketNotes/group/${note.initial}`} onClick={()=>setSelectedGroup(note)} className='flex items-center gap-4 p-4 rounded-lg hover:bg-gray-100'>
            <div className=" text-white w-[50px] h-[50px] flex items-center justify-center rounded-full font-bold" style={{background:note.color}}>
              {note.initial}
            </div>
            <div className="text-md text-black font-medium">{note.name}</div>
          </Link>


        ))}
      </div>
      <div className='flex justify-end items-center h-[10%]'>
        <button className='relative bg-[#16008B] w-[65px] h-[65px] rounded-full flex items-center justify-center'
        onClick={()=>setShowPopup(true)}>
          <img src={plus} alt="Add" className='absolute' />
        </button>
      </div>

      {showPopup && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-500 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg w-1/3">
            <label className="w-1/3 text-md font-semibold flex" >Create New Group</label>
            <div className="flex items-center gap-6 mb-4">
            <label className="w-1/3 text-md font-semibold">Group Name</label>
            <input
              type="text"
              placeholder="Enter Group Name"
              value={newGroupName}
              onChange={(e) => setNewGroupName(e.target.value)}
              className="border p-2 pl-3 w-2/3 "
            />

            </div>
           
            <div className="flex gap-4 mb-4">
              {['red', 'blue', 'green', 'yellow'].map((color) => (
                <div
                  key={color}
                  className={`w-10 h-10 rounded-full cursor-pointer bg-${color}-500`}
                  onClick={() => setNewGroupColor(color)}
                />
              ))}
            </div>
            <div className="flex justify-end gap-4">
              <button onClick={() => setShowPopup(false)} className="px-4 py-2 bg-blue-500 rounded-md">Cancel</button>
              <button onClick={handleAddGroup} className="px-4 py-2 bg-blue-500 text-white rounded-md">Create</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
