import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaTimes } from 'react-icons/fa';

const dummyImages = Array.from({ length: 50 }, (_, i) => `https://picsum.photos/200/200?random=${i + 1}`);

const Image = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  // State to hold images
  const [images, setImages] = useState(dummyImages);

  // State for preview popup
  const [previewImage, setPreviewImage] = useState(null);

  // Navigate to full image
  const handleFullImage = (url) => {
    navigate(`/full-image?src=${encodeURIComponent(url)}`);
  };

  // Remove image from list
  const handleDeleteImage = (url) => {
    setImages(images.filter((img) => img !== url));
    if (previewImage === url) {
      setPreviewImage(null);
    }
  };

  return (
    <div className="p-4 relative">
      <h2 className="text-xl font-bold mb-4">Hi, {username}</h2>

      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
        {images.map((img, idx) => (
          <div key={idx} className="relative border rounded overflow-hidden shadow">
            {/* Close Button on Image */}
            <button
              className="absolute top-1 right-1 bg-white rounded-full p-1 text-sm hover:bg-red-500 hover:text-white transition"
              onClick={() => handleDeleteImage(img)}
            >
              <FaTimes />
            </button>

            <img
              src={img}
              alt={`Random ${idx}`}
              loading="lazy"
              className="w-full h-40 object-cover"
            />

            {/* Buttons */}
            <div className="flex justify-between px-2 py-2">
              <button
                className="bg-blue-500 text-white px-2 py-1 rounded text-sm"
                onClick={() => setPreviewImage(img)}
              >
                Preview
              </button>
              <button
                className="bg-green-500 text-white px-2 py-1 rounded text-sm"
                onClick={() => handleFullImage(img)}
              >
                Full Image
              </button>
            </div>
          </div>
        ))}
      </div>

