import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import ChatHeader from '../component/chatHeader';
import ChatPanel from '../component/chatPanel';

const groupDetails = {
  MN: 'My Notes',
  MP: 'My Personal Group',
  JG: 'Javascript Group',
  HG: 'HTML Group',
  CN: 'CSS Notes',
  SN: 'SQL Notes',
  PN: 'Python Notes'
};

const GroupChat = ({group}) => {
  const { groupId } = useParams();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() === '') return;
    setMessages([...messages, input]);
    setInput('');
  };

  return (
    <div className='flex flex-col h-full w-full'>
      <ChatHeader initial={group.initial} name={groupDetails[groupId]} />
      <ChatPanel messages={messages} />
      <div className='h-[10%] bg-[#DAE5F5] flex items-center px-6'>
        <input
          className='flex-1 p-4 rounded-xl border border-gray-300 text-lg'
          type="text"
          placeholder="Enter your text here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button
          onClick={handleSend}
          className='ml-4 px-4 py-2 bg-blue-600 text-white font-semibold rounded-xl shadow'
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default GroupChat;
