import React, { useState } from 'react';
import Sidebar from './component/sidebar';// your reusable Sidebar component
import Home from './pages/home';
import GroupChat from './pages/groupChat';

const Layout = () => {
    const [groups, setGroups] = useState([]);
    // const [selectedGroup, setSelectedGroup] = useState({});
    const [selectedGroup, setSelectedGroup] = useState(null);
    const [groupMessages, setGroupMessages] = useState({}); // stores messages per group
    const handleSendMessage = (groupName, message) => {
        setGroupMessages((prev) => ({
          ...prev,
          [groupName]: [...(prev[groupName] || []), message],
        }));
      };
    
    return (
    <div className='flex h-screen w-screen'>
      <Sidebar groups={groups} setGroups={setGroups} setSelectedGroup={setSelectedGroup}/>
      {/* <div className='flex-1 bg-[#DAE5F5]'>
      {groups.length > 0 ? <GroupChat group={selectedGroup}/>:<Home/>}
      </div> */}
       <>
        {selectedGroup ? (
          <GroupChat
            group={selectedGroup}
            messages={groupMessages[selectedGroup.name] || []}
            onSendMessage={handleSendMessage}
          />
        ) : (
          <Home />
        )}
      </>
    </div>
  );
};

export default Layout;
