import React, { useEffect, useState} from 'react';

const AdvanceHooks = () => {
    const [count, setCount] = useState(0);
    const [county, setCounty] = useState(0);

    const increment=()=>{
        setCount(count+1);
        setCounty(county+1);
    }

    const decrement=() => {
        setCount(count-1);
    }

    function findElement() {
        const target = 99999999;
      
        // Create array from 1 to 100,000,000
        const numbers = Array.from({ length: 100_000_000 }, (_, i) => i + 1);
      
        // Use indexOf to find the element
        const index = numbers.indexOf(target);
      
        if (index !== -1) {
          console.log(`Element ${target} found at index ${index}`);
        } else {
          console.log(`Element ${target} not found`);
        }
      }

    useEffect(() => {
      findElement();
    }, [county])
    
  return (
    <div>
      <h1>Count: {count}</h1>
      <button onClick={increment}>Increment </button>
      <button onClick={decrement}>Decrement</button>
    </div>
  )
}

export default AdvanceHooks
