import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import ChatHeader from '../component/chatHeader';
import ChatPanel from '../component/chatPanel';


const GroupChat = ({group}) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const send ='./Assets/Images/send.png'

  const handleSend = () => {
    if (input.trim() === '') return;
    setMessages([...messages, input]);
    setInput('');
  };

  return (
    <div className='flex flex-col h-full w-full'>
      <ChatHeader initial={group.initial} name={group.name} color={group.color}/>
      <ChatPanel messages={messages} />
      <div className='h-[10%] bg-[#DAE5F5] flex items-center px-6'>
        <div realative>

        <input
          className='w-full flex-1 p-4 rounded-xl border border-gray-300 '
          type="text"
          placeholder="Enter your text here..."
          value={input}
          
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          
        />
        <button className='ml-4 bg-[#DAE5F5] font-semibold   '
          onClick={handleSend}
        >
             <img src={send} alt="Add"  />
          
        </button>
        </div>
        
        {/* <div className='flex justify-end items-center h-[10%] w-[100%]'>
        <button className='relative bg-[#16008B] w-[65px] h-[65px] rounded-full flex items-center justify-center'
        onClick={()=>setShowPopup(true)}>
          <img src={plus} alt="Add" className='absolute' />
        </button>
      </div> */}
      </div>
    </div>
  );
};

export default GroupChat;
